package kotlinadventofcode.`2015`

import kotlinadventofcode.Day

class `2015-01` : Day {

    override fun runPart1(input: String): String {
        var floor = 0
        for (char: Char in input) {
            when (char) {
                '(' -> floor++
                ')' -> floor--
                else -> throw Exception("Got unexpected char: $char")
            }
        }

        return floor.toString()
    }

    override fun runPart2(input: String): String {
        var floor = 0
        for ((index, c) in input.withIndex()) {
            when (c) {
                '(' -> floor++
                ')' -> floor--
                else -> throw Exception("Got unexpected char: $c")
            }

            if (floor == -1) return (index + 1).toString()
        }

        throw Exception("Never got there.")
    }

